import { getActivityList, getActivityInfo, patchActivity } from './service/activity';
class ServerAdapter {
  static instance: any = null;
  //TODO: host?
  private request: any
  private host: any;

  constructor(request: any, host: any) {
    this.request = request
    this.host = host
  }

  static getInstance(request: any, host: any) {
    if (!this.instance) {
      this.instance = new ServerAdapter(request, host);
    }
    return this.instance;
  }

  /** 活动 */
  getActivityList(params) {
    return getActivityList(this.request, params)
  }

  getActivityInfo(id) {
    return getActivityInfo(this.request, id)
  }

  patchActivity(body) {
    return patchActivity(this.request, body)
  }

}
export default ServerAdapter

